declare @i int = (select ProductId from dbo.Product where Name = '<@Name>'); 
delete from dbo.ProductInventory where ProductId = @i; 
delete from dbo.Product where ProductId = @i;

declare @table table ([Name] varchar(200) primary key, [Quantity] int, [CreatedOn] varchar(50));
insert into @table
select p.[Name], isnull(i.[Quantity], 0), replace(rtrim(replace(convert(varchar(50), p.[CreatedOn], 126),'0',' ')),' ','0') from dbo.Product p left outer join dbo.ProductInventory i on p.ProductId = i.ProductId;
declare @max int = (select max(Quantity) from @table);
select	* from	@table where Quantity = @max for json path;

